const rpcUrl = "https://ropsten.infura.io/v3/e94d0d07e6d344de8f7bde30a57c0e7b";
let web3 = new Web3(rpcUrl);


const address = "0xF870Ab92b8137b0243018937bcc8ae5bd1eE4D32";

//const byteCode = "";
const abi = [
	{
		"inputs": [
			{
				"internalType": "string",
				"name": "_text",
				"type": "string"
			},
			{
				"internalType": "int256",
				"name": "_amount",
				"type": "int256"
			}
		],
		"name": "setTransaction",
		"outputs": [],
		"stateMutability": "nonpayable",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "getTransaction",
		"outputs": [
			{
				"internalType": "string",
				"name": "_text",
				"type": "string"
			},
			{
				"internalType": "int256",
				"name": "_amount",
				"type": "int256"
			}
		],
		"stateMutability": "view",
		"type": "function"
	}
];

let contract =new web3.eth.Contract(abi, address);

contract.methods.getTransaction().call(((err, {_text, _amount})=>{
    console.log("getTransaction() => ", _text, _amount);
    document.querySelector('#res').innerHTML = `getTransaction() function output is ${_text} | ${_amount}`;
}));
